<script>import { inertia } from "../index";
export let href;
export let as = "a";
export let data = {};
export let method = "get";
export let replace = false;
export let preserveScroll = false;
export let preserveState = null;
export let only = [];
export let except = [];
export let headers = {};
export let queryStringArrayFormat = "brackets";
export let async = false;
export let prefetch = false;
export let cacheFor = 0;
$: asProp = method !== "get" ? "button" : as.toLowerCase();
$: elProps = {
  a: { href },
  button: { type: "button" }
}[asProp] || {};
</script>

<!-- svelte-ignore a11y-no-static-element-interactions -->
<svelte:element
  this={asProp}
  use:inertia={{
    ...(as !== 'a' ? { href } : {}),
    data,
    method,
    replace,
    preserveScroll,
    preserveState: preserveState ?? method !== 'get',
    only,
    except,
    headers,
    queryStringArrayFormat,
    async,
    prefetch,
    cacheFor,
  }}
  {...$$restProps}
  {...elProps}
  on:focus
  on:blur
  on:click
  on:dblclick
  on:mousedown
  on:mousemove
  on:mouseout
  on:mouseover
  on:mouseup
  on:cancel-token
  on:before
  on:start
  on:progress
  on:finish
  on:cancel
  on:success
  on:error
>
  <slot />
</svelte:element>
